import React, {Component} from 'react'
import Movie from'./Movie'

class Movies extends Component{
    constructor(props){
        super(props)
        this.state = {
           movies: [
                {
                    título:"Bohemian Rhapsody",
                    duracion:"2h 13m",
                    imagen: "data:image/jpeg;base64,", 
                    año: "2018",   //state es la manera correcta para hacer un contenedor
                    director: "Bryan Singer",
                    productora:"GK Films",
                },
                {
                    título:"The Runaways",
                    duracion:"1h 49m",
                    imagen: "data:image/jpeg;base64,", 
                    año: "2010",   //state es la manera correcta para hacer un contenedor
                    director: "Floria Sigismondi",
                    productora:"Apparition",
                },
                {
                    título:"Split",
                    duracion:"1h 57m",
                    imagen: "data:image/jpeg;base64,", 
                    año: "2016",   //state es la manera correcta para hacer un contenedor
                    director: "M. Night Shyamalan",
                    productora:"‎Blinding Edge Pictures",
                },
                {
                    título:"Glass",
                    duracion:"2h 09m",
                    imagen: "data:image/jpeg;base64,", 
                    año: "2019",   //state es la manera correcta para hacer un contenedor
                    director: "M. Night Shyamalan",
                    productora:"‎Blinding Edge Pictures",
                },
                {
                    título:"Sexto Sentido",
                    duracion:"2h 10m",
                    imagen: "data:image/jpeg;base64,", 
                    año: "1999",   //state es la manera correcta para hacer un contenedor
                    director: "M.Night Shyalaman",
                    productora:"Apparition",
                },
                {
                    título:"El fin de los tiempos",
                    duracion:"1h 50m",
                    imagen: "data:image/jpeg;base64,", 
                    año: "2008",   //state es la manera correcta para hacer un contenedor
                    director: "M.Night Shyalaman",
                    productora:"Apparition",
                },
                {
                    título:"La teoría del todo",
                    duracion:"2h 03m",
                    imagen: "data:image/jpeg;base64,", 
                    año: "2014",   //state es la manera correcta para hacer un contenedor
                    director: "James Marsh",
                    productora:"Warner Bros",
                },
                {
                    título:"El hombre que conocia el infinito",
                    duracion:"1h 54m",
                    imagen: "data:image/jpeg;base64,", 
                    año: "2015",   //state es la manera correcta para hacer un contenedor
                    director: "Matt Brown, Matthew Brown",
                    productora:"Fox",
                },
                {
                    título:"Interestelar",
                    duracion:"2h 49m",
                    imagen: "data:image/jpeg;base64,", 
                    año: "2014",   //state es la manera correcta para hacer un contenedor
                    director: "Christopher Nolan",
                    productora:"Dreamworks",
                },
               

               
               
            ],
            newMovie: {
                titulo: null,
                duracion: null,
                imagen: null
            }
        }
        
    }





    changeTitle = (e) => {
        let new_movie = this.state.newMovie
        new_movie.titulo = e.target.value
        this.setState({newMovie: new_movie})
    }
    changeDuracion = (e) => {
        let new_movie = this.state.newMovie
        new_movie.duracion = e.target.value
        this.setState({newMovie: new_movie})
    }
    changeImage = (e) => {
        let new_movie = this.state.newMovie
        new_movie.imagen = e.target.value
        this.setState({newMovie: new_movie})
    }
    changeAño = (e) => {
        let new_movie = this.state.newMovie
        new_movie.año = e.target.value
        this.setState({newMovie: new_movie})
    }
    changeDirector = (e) => {
        let new_movie = this.state.newMovie
        new_movie.director = e.target.value
        this.setState({newMovie: new_movie})
    }
    changeProductora = (e) => {
        let new_movie = this.state.newMovie
        new_movie.productora = e.target.value
        this.setState({newMovie: new_movie})
    }
    addMovie = (e) => {
        e.preventDefault(); //push agregar algo en un arreglo (al final)
        let { movies } =this.state
        movies.push(this.state.newMovie)
        this.setState({movies})
    }

    render(){
        return (
            <div>
                <h1>Estas son las peliculas que puedes ver</h1>
                {this.state.movies.map((movie, index)=>(
                    <div>
                        <img src={movie.imagen} alt={movie.titulo} width="100" height="100"/>
                        <h2>{movie.título}</h2>
                        <h3>{movie.titulo}</h3>
                        <p>{movie.duracion}</p>
                        <p>___________________________________</p>
                        <Movie mo ={movie}/>
                    </div>
                ))}


<div className="form-container">
                    <h2>Agrega una nueva película: </h2>
                    <label for="titulo"> Titulo: </label>
                    <input
                        type="text"
                        name="titulo"
                        id="titulo"
                        placeholder="Titulo"
                        onChange={(e) => this.changeTitle(e)}
                    />

                    <label for="duracion"> Duracion: </label>
                    <input
                        type="text"
                        name="duracion"
                        id="duracion"
                        placeholder="Duracion"
                        onChange={(e) => this.changeDuracion(e)}
                        
                    />

                    <label for="imagen"> Imagen: </label>
                    <input
                        type="text"
                        name="imagen"
                        id="imagen"
                        placeholder="Imagen"
                        onChange={(e)=>this.changeImage(e)}
                    />

                    <label for="año"> Año: </label>
                    <input
                        type="text"
                        name="año"
                        id="año"
                        placeholder="Año"
                        onChange={(e)=>this.changeAño(e)}
                    />

                    <label for="director"> Director: </label>
                    <input
                        type="text"
                        name="director"
                        id="director"
                        placeholder="Director"
                        onChange={(e)=>this.changeDirector(e)}
                    />
                    <label for="productora"> Productora: </label>
                    <input
                        type="text"
                        name="productora"
                        id="productora"
                        placeholder="Productora"
                        onChange={(e)=>this.changeProductora(e)}
                    />
                   
                    <button onClick={(e)=>this.addMovie(e)}>Agregar Película</button>
                </div>
                  
        
            </div>
        )

    }
}

export default Movies